mapper.update:
UPDATE TBL_CLASS_REPLY
SET CLASS_REPLY_CONTENT = #{classReplyContent}, CLASS_REPLY_UPDATE_DATE = SYSDATE
WHERE CLASS_REPLY_NUMBER = #{classReplyNumber}

mapper.select:
SELECT CLASS_REPLY_NUMBER, CLASS_REPLY_CONTENT, CLASS_REPLY_REGISTER_DATE, CLASS_REPLY_UPDATE_DATE, CLASS_NUMBER, USER_NUMBER, USER_NICKNAME FROM ( SELECT ROWNUM RNUM, CLASS_REPLY_NUMBER, CLASS_REPLY_CONTENT, CLASS_REPLY_REGISTER_DATE, CLASS_REPLY_UPDATE_DATE, CLASS_NUMBER, USER_NUMBER, USER_NICKNAME FROM ( SELECT CLASS_REPLY_NUMBER, CLASS_REPLY_CONTENT, CLASS_REPLY_REGISTER_DATE, CLASS_REPLY_UPDATE_DATE, CLASS_NUMBER, U.USER_NUMBER, U.USER_NICKNAME FROM TBL_CLASS_REPLY R JOIN TBL_USERS U ON CLASS_NUMBER = #{classNumber} AND R.USER_NUMBER = U.USER_NUMBER ORDER BY CLASS_REPLY_NUMBER DESC )
WHERE ROWNUM <= #{criteria.page} * #{criteria.amount}
) WHERE RNUM > (#{criteria.page} - 1) * #{criteria.amount}
