mapper.update:
UPDATE TBL_STUDY_REPLY
SET STUDY_REPLY_CONTENT = #{studyReplyContent}, STUDY_REPLY_UPDATE_DATE = SYSDATE
WHERE STUDY_REPLY_NUMBER = #{studyReplyNumber}

mapper.select:
SELECT STUDY_REPLY_NUMBER, STUDY_REPLY_CONTENT, STUDY_REPLY_REGISTER_DATE, STUDY_REPLY_UPDATE_DATE, STUDY_NUMBER, USER_NUMBER, USER_NICKNAME FROM ( SELECT ROWNUM RNUM, STUDY_REPLY_NUMBER, STUDY_REPLY_CONTENT, STUDY_REPLY_REGISTER_DATE, STUDY_REPLY_UPDATE_DATE, STUDY_NUMBER, USER_NUMBER, USER_NICKNAME FROM ( SELECT STUDY_REPLY_NUMBER, STUDY_REPLY_CONTENT, STUDY_REPLY_REGISTER_DATE, STUDY_REPLY_UPDATE_DATE, STUDY_NUMBER, R.USER_NUMBER, USER_NICKNAME FROM TBL_STUDY_REPLY R JOIN TBL_USERS U ON STUDY_NUMBER = #{studyNumber} AND R.USER_NUMBER = U.USER_NUMBER ORDER BY STUDY_REPLY_NUMBER DESC )
WHERE ROWNUM <= #{criteria.page} * #{criteria.amount}
) WHERE RNUM > (#{criteria.page} - 1) * #{criteria.amount}
